                                'rgb(59, 130, 246)',
                                'rgb(16, 185, 129)',
                                'rgb(245, 158, 11)',
                                'rgb(139, 92, 246)'
                            ],
                            borderWidth: 2,
                            borderRadius: 8,
                            borderSkipped: false,
                        }]
                    };

                    const config = {
                        type: 'bar',
                        data: data,
                        options: {
                            responsive: true,
                            maintainAspectRatio: false,
                            scales: {
                                y: {
                                    beginAtZero: true,
                                    grid: {
                                        color: 'rgba(0, 0, 0, 0.1)'
                                    },
                                    ticks: {
                                        font: {
                                            size: 12,
                                            weight: '500'
                                        }
                                    }
                                },
                                x: {
                                    grid: {
                                        display: false
                                    },
                                    ticks: {
                                        font: {
                                            size: 12,
                                            weight: '500'
                                        }
                                    }
                                }
                            },
                            plugins: {
                                legend: {
                                    display: false
                                },
                                tooltip: {
                                    backgroundColor: 'rgba(0, 0, 0, 0.8)',
                                    titleColor: '#fff',
                                    bodyColor: '#fff',
                                    cornerRadius: 8,
                                    callbacks: {
                                        label: function (context) {
                                            return `Count: ${context.parsed.y}`;
                                        }
                                    }
                                }
                            },
                            animation: {
                                duration: 2000,
                                easing: 'easeInOutQuart',
                                delay: function (context) {
                                    return context.dataIndex * 200;
                                }
                            },
                            onHover: (event, activeElements) => {
                                event.native.target.style.cursor = activeElements.length > 0 ? 'pointer' : 'default';
                            }
                        }
                    };

                    this.charts.licenseDistribution = new Chart(ctx, config);
                })
                .catch(error => {
                    // Use fallback data when API fails
                    const fallbackData = {
                        labels: ['Regular', 'Extended'],
                        data: [0, 0]
                    };

                    const data = {
                        labels: fallbackData.labels,
                        datasets: [{
                            label: 'License Count',
                            data: fallbackData.data,
                            backgroundColor: [
                                'rgba(59, 130, 246, 0.6)',
                                'rgba(16, 185, 129, 0.6)'
                            ],
                            borderColor: [
                                'rgb(59, 130, 246)',
                                'rgb(16, 185, 129)'
                            ],
                            borderWidth: 2,
                            borderRadius: 8,
                            borderSkipped: false,
                        }]
                    };

                    const config = {
                        type: 'bar',
                        data: data,
                        options: {
                            responsive: true,
                            maintainAspectRatio: false,
                            scales: {
                                y: {
                                    beginAtZero: true,
                                    grid: {
                                        color: 'rgba(0, 0, 0, 0.1)'
                                    },
                                    ticks: {
                                        font: {
                                            size: 12,
                                            weight: '500'
                                        }
                                    }
                                },
                                x: {
                                    grid: {
                                        display: false
                                    },
                                    ticks: {
                                        font: {
                                            size: 12,
                                            weight: '500'
                                        }
                                    }
                                }
                            },
                            plugins: {
                                legend: {
                                    display: false
                                },
                                tooltip: {
                                    backgroundColor: 'rgba(0, 0, 0, 0.8)',
                                    titleColor: '#fff',
                                    bodyColor: '#fff',
                                    cornerRadius: 8,
                                    callbacks: {
                                        label: function (context) {
                                            return `Count: ${context.parsed.y}`;
                                        }
                                    }
                                }
                            },
                            animation: {
                                duration: 1000,
                                easing: 'easeInOutQuart'
                            }
                        }
                    };

                    this.charts.licenseDistribution = new Chart(ctx, config);

                    // Show user-friendly message only in development
                    if (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1') {
                        if (window.adminDashboard && window.adminDashboard.showToast) {
                            window.adminDashboard.showToast('Using fallback data for License Distribution chart', 'info', 3000);
                        }
                    }
                });
        }

        createRevenueChart() {
            const ctx = document.getElementById('revenueChart');
            if (!ctx) return;

            // Check if chart already exists and destroy it
            if (Chart.getChart(ctx)) {
                Chart.getChart(ctx).destroy();
            }

            // Fetch real data from API
            this.fetchRevenueData('monthly');

            // Store chart context for updates
            this.revenueChartCtx = ctx;
        }

        fetchRevenueData(period = 'monthly') {
            this.apiFetch(`/admin/dashboard/revenue?period=${encodeURIComponent(period)}`)
