// Simple License Template
function checkLicense($key) {
    return strlen($key) >= 10 && preg_match('/^[A-Z0-9]+$/', $key);
}

function generateLicense() {
    return strtoupper(substr(md5(uniqid()), 0, 16));
}`
        };
        
