// Copy to clipboard functionality
function copyToClipboard(elementId) {
    const element = document.getElementById(elementId);
    const text = element.textContent;
    
    // Create a temporary textarea element
    const textarea = document.createElement('textarea');
    textarea.value = text;
    document.body.appendChild(textarea);
    
    // Select and copy the text
    textarea.select();
    document.execCommand('copy');
    
    // Remove the temporary element
    document.body.removeChild(textarea);
    
    // Show success feedback
    const button = document.querySelector(`[onclick="copyToClipboard('${elementId}')"]`);
    if (button) {
        const originalText = button.textContent;
        button.textContent = 'Copied!';
        button.classList.add('copy-button-success');
        
        setTimeout(() => {
            button.textContent = originalText;
            button.classList.remove('copy-button-success');
        }, 2000);
    }
}

// Initialize when DOM is loaded
document.addEventListener('DOMContentLoaded', function() {
    // Add click event listeners to all copy buttons
    const copyButtons = document.querySelectorAll('[onclick*="copyToClipboard"]');
    copyButtons.forEach(button => {
        button.addEventListener('click', function(e) {
            e.preventDefault();
            const elementId = this.getAttribute('onclick').match(/'([^']+)'/)[1];
            copyToClipboard(elementId);
        });
    });
    
    // Add smooth scrolling for anchor links
    const anchorLinks = document.querySelectorAll('a[href^="#"]');
    anchorLinks.forEach(link => {
        link.addEventListener('click', function(e) {
            e.preventDefault();
            const targetId = this.getAttribute('href').substring(1);
            const targetElement = document.getElementById(targetId);
            
            if (targetElement) {
                targetElement.scrollIntoView({
                    behavior: 'smooth',
                    block: 'start'
                });
            }
        });
    });
    
    // Add syntax highlighting for code blocks (basic)
    const codeBlocks = document.querySelectorAll('.guide-code-content code');
    codeBlocks.forEach(block => {
        // Basic syntax highlighting for common keywords
        let content = block.textContent;
        
        // Highlight PHP keywords
        content = content.replace(/\b(<?php|<?=|\$[a-zA-Z_][a-zA-Z0-9_]*|function|class|public|private|protected|return|if|else|foreach|for|while|try|catch|throw|new|use|namespace|require|include)\b/g, 
            '<span class="syntax-keyword">$1</span>');
        
        // Highlight strings
        content = content.replace(/(['"])([^'"]*)\1/g, 
            '<span class="syntax-string">$1$2$1</span>');
        
        // Highlight comments
        content = content.replace(/(\/\/.*$|\/\*[\s\S]*?\*\/)/gm, 
            '<span class="syntax-comment">$1</span>');
        
        block.innerHTML = content;
    });
    
    // Add tooltips for API methods
    const apiMethods = document.querySelectorAll('.guide-api-method');
    apiMethods.forEach(method => {
        method.setAttribute('title', 'Click to copy URL');
        method.style.cursor = 'pointer';
        
        method.addEventListener('click', function() {
            const url = this.querySelector('.guide-api-method-url').textContent;
            navigator.clipboard.writeText(url).then(() => {
                // Show temporary feedback
                this.classList.add('copied');
                setTimeout(() => {
                    this.classList.remove('copied');
                }, 1000);
            });
        });
    });
    
    // Add expand/collapse functionality for long code blocks
    const longCodeBlocks = document.querySelectorAll('.guide-code-content');
    longCodeBlocks.forEach(block => {
        if (block.scrollHeight > 300) {
            const wrapper = block.closest('.guide-code-block');
            const header = wrapper.querySelector('.guide-code-header');
            
            // Add expand/collapse button
            const expandBtn = document.createElement('button');
            expandBtn.className = 'guide-code-expand';
            expandBtn.textContent = 'Show More';
            
            header.appendChild(expandBtn);
            
            // Set initial state
            block.style.maxHeight = '300px';
            block.style.overflow = 'hidden';
            
            // Toggle functionality
            expandBtn.addEventListener('click', function() {
                if (block.style.maxHeight === '300px') {
                    block.style.maxHeight = 'none';
                    this.textContent = 'Show Less';
                } else {
                    block.style.maxHeight = '300px';
                    this.textContent = 'Show More';
                }
            });
        }
    });
});

/**
 * Professional Preloader System
 * High-quality, customizable preloader with multiple styles
 * Envato-compliant JavaScript with validation and best practices
 */

class PreloaderManager {
    constructor() {
        this.container = document.getElementById('preloader-container');
        this.settings = window.preloaderSettings || {};
        this.isVisible = true;
        this.init();
    }

    init() {
        if (!this.container || !this.settings.enabled) {
            return;
        }

        this.setupDynamicStyles();
        this.setupEventListeners();
        this.startPreloader();
    }

    setupDynamicStyles() {
        if (!this.settings.color || !this.settings.backgroundColor) {
            return;
        }

        const style = document.createElement('style');
        style.textContent = `
            :root {
                --preloader-color: ${this.settings.color};
                --preloader-bg: ${this.settings.backgroundColor};
                --preloader-text-color: ${this.settings.color};
            }
            
            @media (prefers-color-scheme: dark) {
                :root {
                    --preloader-bg-dark: ${this.settings.backgroundColor === '#ffffff' ? '#1f2937' : this.settings.backgroundColor};
                    --preloader-text-color-dark: ${this.settings.color === '#3b82f6' ? '#d1d5db' : this.settings.color};
                }
            }
        `;
        document.head.appendChild(style);
    }

    setupEventListeners() {
        // Hide preloader when page is fully loaded
        window.addEventListener('load', () => {
            this.hidePreloader();
        });

        // Fallback: Hide preloader after maximum duration
        if (this.settings.duration) {
            setTimeout(() => {
                this.hidePreloader();
            }, this.settings.duration);
        }

        // Handle page visibility changes
        document.addEventListener('visibilitychange', () => {
            if (document.hidden) {
                this.pauseAnimations();
            } else {
                this.resumeAnimations();
            }
        });
    }

    startPreloader() {
        if (!this.container) {
            return;
        }

        // Add entrance animation
        this.container.style.opacity = '1';
        this.container.style.visibility = 'visible';

        // Start progress animation if it's a progress type
        if (this.settings.type === 'progress') {
            this.animateProgress();
        }
    }

    hidePreloader() {
        if (!this.container || !this.isVisible) {
            return;
        }

        this.isVisible = false;
        this.container.classList.add('hidden');

        // Remove from DOM after animation
        setTimeout(() => {
            if (this.container && this.container.parentNode) {
                this.container.parentNode.removeChild(this.container);
            }
        }, 500);
    }

    animateProgress() {
        const progressBar = this.container.querySelector('.preloader-progress-bar');
        if (!progressBar) {
            return;
        }

        let progress = 0;
        const interval = setInterval(() => {
            progress += Math.random() * 15;
            if (progress >= 100) {
                progress = 100;
                clearInterval(interval);
            }
            progressBar.style.width = progress + '%';
        }, 100);
    }

    pauseAnimations() {
        const animations = this.container.querySelectorAll('[style*="animation"]');
        animations.forEach(el => {
            el.style.animationPlayState = 'paused';
        });
    }

    resumeAnimations() {
        const animations = this.container.querySelectorAll('[style*="animation"]');
        animations.forEach(el => {
            el.style.animationPlayState = 'running';
        });
    }

    // Public method to manually hide preloader
    static hide() {
        const preloader = document.getElementById('preloader-container');
        if (preloader) {
            preloader.classList.add('hidden');
            setTimeout(() => {
                if (preloader.parentNode) {
                    preloader.parentNode.removeChild(preloader);
                }
            }, 500);
        }
    }

    // Public method to show preloader
    static show() {
        const preloader = document.getElementById('preloader-container');
        if (preloader) {
            preloader.classList.remove('hidden');
            preloader.style.opacity = '1';
            preloader.style.visibility = 'visible';
        }
    }
}

// Initialize preloader when DOM is ready
document.addEventListener('DOMContentLoaded', () => {
    new PreloaderManager();
});

