// Search Enhancements
function initializeSearchEnhancements() {
    const searchForm = document.querySelector('.kb-search-form');
    const searchInput = document.querySelector('.kb-search-input');
    const searchButton = document.querySelector('.kb-search-button');
    
    if (!searchForm || !searchInput) return;
    
    // Auto-focus search input
    if (searchInput && !searchInput.value) {
        searchInput.focus();
    }
    
    // Search form submission with loading state
    searchForm.addEventListener('submit', function(e) {
        if (searchButton) {
            searchButton.disabled = true;
            searchButton.innerHTML = '<i class="fas fa-spinner fa-spin" aria-hidden="true"></i> <span class="button-text">' + (searchButton.querySelector('.button-text')?.textContent || 'Searching') + '...</span>';
        }
    });
    
    // Search input enhancements
    searchInput.addEventListener('input', function() {
        const value = this.value.trim();
        if (searchButton) {
            searchButton.disabled = value.length === 0;
        }
    });
    
    // Keyboard shortcuts
    document.addEventListener('keydown', function(e) {
        // Ctrl/Cmd + K to focus search
        if ((e.ctrlKey || e.metaKey) && e.key === 'k') {
            e.preventDefault();
            if (searchInput) {
                searchInput.focus();
                searchInput.select();
            }
        }
        
        // Escape to clear search
        if (e.key === 'Escape' && document.activeElement === searchInput) {
            searchInput.value = '';
            searchInput.blur();
        }
    });
    
    // Search suggestions (if implemented)
    initializeSearchSuggestions();
    
    // Search result animations
    initializeSearchResultAnimations();
}

// Search Suggestions
function initializeSearchSuggestions() {
    const searchInput = document.querySelector('.kb-search-input');
    if (!searchInput) return;
    
    // Add search suggestions container
    const suggestionsContainer = document.createElement('div');
    suggestionsContainer.className = 'kb-search-suggestions-container';
    suggestionsContainer.style.display = 'none';
    
    const searchWrapper = document.querySelector('.kb-search-wrapper');
    if (searchWrapper) {
        searchWrapper.appendChild(suggestionsContainer);
    }
    
    // Handle input with debouncing
    let searchTimeout;
    searchInput.addEventListener('input', function() {
        clearTimeout(searchTimeout);
        const query = this.value.trim();
        
        if (query.length < 2) {
            suggestionsContainer.style.display = 'none';
            return;
        }
        
        searchTimeout = setTimeout(() => {
            // Here you could implement AJAX search suggestions
            // For now, we'll just show/hide based on input length
            if (query.length >= 2) {
                suggestionsContainer.style.display = 'block';
            }
        }, 300);
    });
    
    // Hide suggestions when clicking outside
    document.addEventListener('click', function(e) {
        if (!searchWrapper.contains(e.target)) {
            suggestionsContainer.style.display = 'none';
        }
    });
}

// Search Result Animations
function initializeSearchResultAnimations() {
    const searchResults = document.querySelectorAll('.user-kb-search-result');
    
    if (searchResults.length === 0) return;
    
    // Intersection Observer for scroll animations
    const observer = new IntersectionObserver((entries) => {
        entries.forEach((entry, index) => {
            if (entry.isIntersecting) {
                setTimeout(() => {
                    entry.target.style.opacity = '1';
                    entry.target.style.transform = 'translateY(0)';
                }, index * 100);
            }
        });
    }, {
        threshold: 0.1,
        rootMargin: '0px 0px -50px 0px'
    });
    
    // Initialize animations
    searchResults.forEach((result, index) => {
        result.style.opacity = '0';
        result.style.transform = 'translateY(20px)';
        result.style.transition = 'opacity 0.6s ease, transform 0.6s ease';
        observer.observe(result);
    });
}

})();

/**
 * Product Files JavaScript
 * Handles file upload, download, and UI interactions
 */

$(document).ready(function() {
    // Initialize product files functionality
    initProductFiles();
});

function initProductFiles() {
    // File upload functionality
    initFileUpload();
    
    // Download functionality
    initDownloadButtons();
    
    // File management
    initFileManagement();
    
    // Statistics
    initStatistics();
}

/**
 * Initialize file upload functionality
 */
function initFileUpload() {
    const fileInput = $('#file');
    const uploadForm = $('#fileUploadForm');
    const uploadArea = $('.file-upload-area');
    
    // Drag and drop functionality
    if (uploadArea.length) {
        uploadArea.on('dragover', function(e) {
            e.preventDefault();
            $(this).addClass('dragover');
        });
        
        uploadArea.on('dragleave', function(e) {
            e.preventDefault();
            $(this).removeClass('dragover');
        });
        
        uploadArea.on('drop', function(e) {
            e.preventDefault();
            $(this).removeClass('dragover');
            
            const files = e.originalEvent.dataTransfer.files;
            if (files.length > 0) {
                fileInput[0].files = files;
                handleFileSelection(files[0]);
            }
        });
        
        uploadArea.on('click', function() {
            fileInput.click();
        });
    }
    
    // File input change
    fileInput.on('change', function() {
        const file = this.files[0];
        if (file) {
            handleFileSelection(file);
        }
    });
    
    // Form submission
    uploadForm.on('submit', function(e) {
        e.preventDefault();
        handleFileUpload();
    });
}

/**
 * Handle file selection
 */
function handleFileSelection(file) {
    const maxSize = 100 * 1024 * 1024; // 100MB
    const allowedTypes = [
        'application/zip',
        'application/x-zip-compressed',
        'application/x-rar-compressed',
        'application/pdf',
        'text/plain',
        'application/json',
        'application/xml',
        'text/xml',
        'application/javascript',
        'text/css',
        'text/html',
        'application/php',
        'application/x-php',
        'text/php',
        'application/sql',
        'text/sql',
        'image/jpeg',
        'image/png',
        'image/gif',
        'image/svg+xml'
    ];
    
    // Validate file size
    if (file.size > maxSize) {
        showAlert('error', 'File size cannot exceed 100MB');
        return;
    }
    
    // Validate file type
    if (!allowedTypes.includes(file.type)) {
        showAlert('error', 'File type not allowed');
        return;
    }
    
    // Show file info
    showFileInfo(file);
}

/**
 * Show file information
 */
function showFileInfo(file) {
    const fileInfo = `
        <div class="file-info mt-2">
            <small class="text-muted">
                <i class="fas fa-file mr-1"></i>
                ${file.name} (${formatFileSize(file.size)})
            </small>
        </div>
    `;
    
    $('.file-upload-area').after(fileInfo);
}

/**
 * Handle file upload
 */
function handleFileUpload() {
    const form = $('#fileUploadForm');
    const formData = new FormData(form[0]);
    const submitBtn = form.find('button[type="submit"]');
    const originalText = submitBtn.html();
    
    // Show loading state
    submitBtn.html('<i class="fas fa-spinner fa-spin mr-1"></i>' + getTranslation('Uploading...')).prop('disabled', true);
    
    // Show progress bar
    showProgressBar();
    
    $.ajax({
        url: form.attr('action'),
        type: 'POST',
        data: formData,
        processData: false,
        contentType: false,
        xhr: function() {
            const xhr = new window.XMLHttpRequest();
            xhr.upload.addEventListener("progress", function(evt) {
                if (evt.lengthComputable) {
                    const percentComplete = evt.loaded / evt.total * 100;
                    updateProgressBar(percentComplete);
                }
            }, false);
            return xhr;
        },
        success: function(response) {
            if (response.success) {
                showAlert('success', response.message);
                setTimeout(() => {
                    location.reload();
                }, 1500);
            } else {
                showAlert('error', response.message);
            }
        },
        error: function(xhr) {
            const errors = xhr.responseJSON?.errors;
            if (errors) {
                Object.keys(errors).forEach(function(key) {
                    showAlert('error', errors[key][0]);
                });
            } else {
                showAlert('error', 'File upload failed');
            }
        },
        complete: function() {
            submitBtn.html(originalText).prop('disabled', false);
            hideProgressBar();
        }
    });
}

/**
 * Initialize download buttons
 */
function initDownloadButtons() {
    // Add loading state to download buttons
    $('a[href*="download"]').on('click', function() {
        const btn = $(this);
        const originalText = btn.html();
        
        btn.html('<i class="fas fa-spinner fa-spin mr-2"></i>' + getTranslation('Downloading...')).prop('disabled', true);
        
        // Re-enable button after 5 seconds (in case download doesn't start)
        setTimeout(function() {
            btn.html(originalText).prop('disabled', false);
        }, 5000);
    });
    
    // Show download confirmation for large files
    $('a[href*="download"]').on('click', function(e) {
        const fileSize = $(this).closest('.card').find('.font-weight-bold').first().text();
        const sizeInMB = parseFloat(fileSize);
        
        if (sizeInMB > 50) { // Files larger than 50MB
            if (!confirm('This file is large (' + fileSize + '). Do you want to continue?')) {
                e.preventDefault();
            }
        }
    });
}

/**
 * Initialize file management
 */
function initFileManagement() {
    // Edit file functionality
    $('.edit-file-btn').on('click', function() {
        const fileId = $(this).data('file-id');
        const description = $(this).data('description');
        const isActive = $(this).data('is-active');
        
        $('#editFileForm').data('file-id', fileId);
        $('#edit_description').val(description);
        $('#edit_is_active').prop('checked', isActive == 1);
        
        $('#editFileModal').modal('show');
    });
    
    // Edit form submission
    $('#editFileForm').on('submit', function(e) {
        e.preventDefault();
        handleFileEdit();
    });
    
    // Delete file functionality
    $('.delete-file-btn').on('click', function() {
        const fileId = $(this).data('file-id');
        const filename = $(this).data('filename');
        
        if (confirm(getTranslation('Are you sure you want to delete the file') + ' "' + filename + '"?\n\n' + getTranslation('This action cannot be undone.'))) {
            handleFileDelete(fileId);
        }
    });
}

/**
 * Handle file edit
 */
function handleFileEdit() {
    const form = $('#editFileForm');
    const fileId = form.data('file-id');
    const formData = form.serialize();
    
    $.ajax({
        url: '/admin/product-files/' + fileId,
        type: 'PUT',
        data: formData,
        success: function(response) {
            if (response.success) {
                showAlert('success', response.message);
                $('#editFileModal').modal('hide');
                setTimeout(() => {
                    location.reload();
                }, 1500);
            } else {
                showAlert('error', response.message);
            }
        },
        error: function(xhr) {
            const errors = xhr.responseJSON?.errors;
            if (errors) {
                Object.keys(errors).forEach(function(key) {
                    showAlert('error', errors[key][0]);
                });
            } else {
                showAlert('error', 'File update failed');
            }
        }
    });
}

/**
 * Handle file delete
 */
function handleFileDelete(fileId) {
    $.ajax({
        url: '/admin/product-files/' + fileId,
        type: 'DELETE',
        data: {
            _token: $('meta[name="csrf-token"]').attr('content')
        },
        success: function(response) {
            if (response.success) {
                showAlert('success', response.message);
                $('tr[data-file-id="' + fileId + '"]').fadeOut(500, function() {
                    $(this).remove();
                });
            } else {
                showAlert('error', response.message);
            }
        },
        error: function() {
            showAlert('error', 'File deletion failed');
        }
    });
}

/**
 * Initialize statistics
 */
function initStatistics() {
    // Animate statistics on page load
    $('.stat-item h4').each(function() {
        const $this = $(this);
        const countTo = parseInt($this.text());
        
        $({ countNum: 0 }).animate({
            countNum: countTo
        }, {
            duration: 2000,
            easing: 'swing',
            step: function() {
                $this.text(Math.floor(this.countNum));
            },
            complete: function() {
                $this.text(this.countNum);
            }
        });
    });
}

/**
 * Show progress bar
 */
function showProgressBar() {
    const progressBar = `
        <div class="upload-progress">
            <div class="upload-progress-bar" style="width: 0%"></div>
        </div>
    `;
    
    $('#fileUploadForm').after(progressBar);
}

/**
 * Update progress bar
 */
function updateProgressBar(percent) {
    $('.upload-progress-bar').css('width', percent + '%');
}

/**
 * Hide progress bar
 */
function hideProgressBar() {
    $('.upload-progress').fadeOut(500, function() {
        $(this).remove();
    });
}

/**
 * Show alert message
 */
function showAlert(type, message) {
    const alertClass = type === 'success' ? 'alert-success' : 'alert-danger';
    const icon = type === 'success' ? 'fa-check-circle' : 'fa-exclamation-triangle';
    
    const alert = `
        <div class="alert ${alertClass} alert-dismissible fade show" role="alert">
            <i class="fas ${icon} mr-2"></i>
            ${message}
            <button type="button" class="close" data-dismiss="alert">
                <span>&times;</span>
            </button>
        </div>
    `;
    
    $('.card-body').prepend(alert);
    
    // Auto-dismiss after 5 seconds
    setTimeout(() => {
        $('.alert').fadeOut(500, function() {
            $(this).remove();
        });
    }, 5000);
}

/**
 * Format file size
 */
function formatFileSize(bytes) {
    if (bytes === 0) return '0 Bytes';
    
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
}

/**
 * Get translation (placeholder function)
 */
function getTranslation(key) {
    // This would typically use Laravel's translation system
    // For now, return the key as fallback
    const translations = {
        'Uploading...': ' ...',
        'Downloading...': ' ...',
        'Are you sure you want to delete the file': '     ',
        'This action cannot be undone.': '     .'
    };
    
    return translations[key] || key;
}

/**
 * User Tickets JavaScript
 * Handles ticket creation form functionality
 */

class UserTickets {
    constructor() {
        this.init();
    }

    init() {
        // Progressive enhancement - ensure form works without JavaScript
        if (typeof document === 'undefined') return;
        
        document.addEventListener('DOMContentLoaded', () => {
            this.setupTicketCreation();
        });
    }

    setupTicketCreation() {
        const categorySelect = document.getElementById('category_id');
        const purchaseCodeSection = document.getElementById('purchase-code-section');
        const productSlugSection = document.getElementById('product-slug-section');
        const purchaseCodeRequired = document.getElementById('purchase-code-required');
        const purchaseCodeInput = document.getElementById('purchase_code');
        const productSlugInput = document.getElementById('product_slug');
        const productNameDisplay = document.getElementById('product-name-display');
        const productNameSpan = document.getElementById('product-name');
        
        // Debug: Check if elements exist
        if (!productSlugInput) {
            return;
        }
        if (!productNameSpan) {
            return;
        }
        if (!productNameDisplay) {
            return;
        }

        // Handle category change
        if (categorySelect) {
            categorySelect.addEventListener('change', () => {
                const selectedOption = categorySelect.options[categorySelect.selectedIndex];
                const requiresPurchaseCode = selectedOption.dataset.requiresPurchaseCode === 'true';
                
                if (requiresPurchaseCode) {
                    purchaseCodeSection.classList.remove('hidden');
                    purchaseCodeRequired.style.display = 'inline';
                    purchaseCodeInput.required = true;
                    productSlugSection.classList.remove('hidden');
                } else {
                    purchaseCodeSection.classList.add('hidden');
                    purchaseCodeRequired.style.display = 'none';
                    purchaseCodeInput.required = false;
                    productSlugSection.classList.add('hidden');
                    productNameDisplay.classList.add('hidden');
                }
            });
        }

        // Handle purchase code verification
        let verificationTimeout;
        if (purchaseCodeInput) {
            purchaseCodeInput.addEventListener('input', () => {
                clearTimeout(verificationTimeout);
                const purchaseCode = purchaseCodeInput.value.trim();
                
                // Reset visual feedback
                productSlugInput.style.borderColor = '';
                productSlugInput.placeholder = 'Product identifier from URL';
                
                if (purchaseCode.length < 10) {
                    productNameDisplay.classList.add('hidden');
                    productSlugInput.value = '';
                    return;
                }

                verificationTimeout = setTimeout(() => {
                    this.verifyPurchaseCode(purchaseCode);
                }, 1000);
            });
        }

        // Set browser info
        const browserInfo = navigator.userAgent + ' | ' + navigator.language + ' | ' + screen.width + 'x' + screen.height;
        const browserInfoInput = document.getElementById('browser_info');
        if (browserInfoInput) {
            browserInfoInput.value = browserInfo;
        }

        // Initialize form state
        if (categorySelect && categorySelect.value) {
            categorySelect.dispatchEvent(new Event('change'));
        }
    }

    verifyPurchaseCode(purchaseCode) {
        const productSlugInput = document.getElementById('product_slug');
        const productNameSpan = document.getElementById('product-name');
        const productNameDisplay = document.getElementById('product-name-display');
        
        // Add visual feedback
        productSlugInput.style.borderColor = '#ffc107';
        productSlugInput.placeholder = 'Verifying purchase code...';
        
        fetch(`/verify-purchase-code/${encodeURIComponent(purchaseCode)}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                if (data.success && data.product) {
                    // Set values
                    productSlugInput.value = data.product.slug;
                    productNameSpan.textContent = data.product.name;
                    productNameDisplay.classList.remove('hidden');
                    
                    // Visual feedback
                    productSlugInput.style.borderColor = '#28a745';
                    productSlugInput.placeholder = 'Product slug filled automatically';
                    
                    // Force update the display
                    productSlugInput.dispatchEvent(new Event('input'));
                } else {
                    productSlugInput.value = '';
                    productNameDisplay.classList.add('hidden');
                    productSlugInput.style.borderColor = '#dc3545';
                    productSlugInput.placeholder = 'Invalid purchase code';
                }
            })
            .catch(error => {
                productSlugInput.value = '';
                productNameDisplay.classList.add('hidden');
                productSlugInput.style.borderColor = '#dc3545';
                productSlugInput.placeholder = 'Error verifying purchase code';
            });
    }
}

// Initialize when DOM is ready
if (typeof document !== 'undefined') {
    new UserTickets();
}
