  // Show notification
  showNotification(message, type) {
    const notification = document.createElement('div');
    notification.className = `admin-notification admin-notification-${type}`;

    const icon =
      type === 'success' ?
        '<i class="fas fa-check-circle"></i>' :
        '<i class="fas fa-times-circle"></i>';

    notificationSecurityUtils.safeInnerHTML(
      this,
      `
            <div class="admin-notification-content">
                <div class="admin-notification-icon">${icon}</div>
                <div class="admin-notification-message">${message}</div>
            </div>
        `,
    );

    notification.style.cssText = `
            position: fixed;
            top: 20px;
            right: 20px;
            padding: 16px 20px;
            border-radius: 12px;
            color: white;
            font-weight: 500;
            z-index: 10000;
            opacity: 0;
            transform: translateX(100%) scale(0.9);
            transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
            box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
            backdrop-filter: blur(10px);
            ${
  type === 'success' ?
    'background: linear-gradient(135deg, #10b981 0%, #059669 100%);' :
    'background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);'
}
        `;

    document.body.appendChild(notification);

    // Animate in
    setTimeout(() => {
      notification.style.opacity = '1';
      notification.style.transform = 'translateX(0) scale(1)';
    }, 100);

    // Remove after 4 seconds
    setTimeout(() => {
      notification.style.opacity = '0';
      notification.style.transform = 'translateX(100%) scale(0.9)';
      setTimeout(() => {
        if (notification.parentNode) {
          notification.parentNode.removeChild(notification);
        }
      }, 400);
    }, 4000);
  }

  // Update Email Preview
  updateEmailPreview() {
    const testDataInputs = document.querySelectorAll(
      'input[name^="test_data"]',
    );
    const previewSubject = document.getElementById('preview-subject');
    const previewBody = document.getElementById('preview-body');

    if (previewSubject) {
      // Get the original subject from data attribute or use a default
      const originalSubject =
        previewSubject.getAttribute('data-original-subject') ||
        '{{ $emailTemplate->subject }}';
      let subject = originalSubject;
      testDataInputs.forEach(input => {
        const variable = input.name.replace('test_data[', '').replace(']', '');
        const value = input.value || `{{${variable}}}`;
        subject = subject.replace(
          new RegExp(`{{${variable}}}`, 'g'),
          value,
        );
      });
      previewSubject.textContent = subject;
    }

    if (previewBody) {
      // Load original template body from data attribute (JSON encoded in Blade view)
      let originalBody;
      try {
        originalBody = JSON.parse(
          previewBody.getAttribute('data-original-body-json') || '""',
        );
      } catch (e) {
        originalBody = '';
      }
      let body = originalBody;
      testDataInputs.forEach(input => {
        const variable = input.name.replace('test_data[', '').replace(']', '');
        const value = input.value || `{{${variable}}}`;
        body = body.replace(
          new RegExp(`\\\\?{{${variable}}}`, 'g'),
          value,
        );
      });
      // Sanitize before injecting
      if (window.SecuritySanitize) {
        previewBody.innerHTML = SecuritySanitize.basicSanitize(body); // security-ignore: JS_DOM_INJECTION (sanitized)
      } else {
        previewBody.textContent = body;
      }
    }
  }

