          contentElementSecurityUtils.safeInnerHTML(
            this,
            `<div class="text-danger"><i class="fas fa-exclamation-triangle"></i> ${data.message || 'Error loading file'}</div>`,
          );
        }
        return true;
      })
      .catch(error => {
