// Update Notification Functions
function checkUpdateNotification() {
  // Only check if we're on admin pages and the notification element exists
  if (!document.getElementById('update-notification')) {
    return;
  }

  // Show notification immediately for testing (like we did before)
  setTimeout(() => {
    showUpdateNotification();
  }, 2000);

  // Get the base URL from the current page
  const baseUrl = window.location.origin;
  const statusUrl = `${baseUrl}/my-logos/public/admin/updates/notifications/status`;

  fetch(statusUrl, {
    method: 'GET',
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
      'X-CSRF-TOKEN':
        document
          .querySelector('meta[name="csrf-token"]')
          ?.getAttribute('content') || '',
      'X-Requested-With': 'XMLHttpRequest',
    },
    credentials: 'same-origin',
  })
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json();
    })
    .then(data => {
      if (data.success && data.data && data.data.should_show_notification) {
        showUpdateNotification();
      }
      return true;
    })
    .catch(error => {
      // Only log error if it's not a 404 or similar expected error
      if (!error.message.includes('404') && !error.message.includes('429')) {
  debugLog('Update notification request failed:', error);
      }
    });
}

function showUpdateNotification() {
  const notification = document.getElementById('update-notification');
  if (notification) {
    notification.classList.add('show');
  }
}

// eslint-disable-next-line no-unused-vars
function dismissUpdateNotification() {
  const notification = document.getElementById('update-notification');
  if (notification) {
    notification.classList.remove('show');
  }

  // Get the base URL from the current page
  const baseUrl = window.location.origin;
  const dismissUrl = `${baseUrl}/my-logos/public/admin/updates/notifications/dismiss`;

  // Dismiss for 24 hours
  fetch(dismissUrl, {
    method: 'POST',
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
      'X-CSRF-TOKEN':
        document
          .querySelector('meta[name="csrf-token"]')
          ?.getAttribute('content') || '',
      'X-Requested-With': 'XMLHttpRequest',
    },
    credentials: 'same-origin',
    body: JSON.stringify({}),
  })
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json();
    })
    .then(data => {
      console.log('Notification dismissed successfully');
      return true;
    })
    .catch(error => {
      console.error('Error dismissing notification:', error);
    });
}

// eslint-disable-next-line no-unused-vars
function dismissUpdateNotificationPermanently() {
  const notification = document.getElementById('update-notification');
  if (notification) {
    notification.classList.remove('show');
  }

  // Get the base URL from the current page
  const baseUrl = window.location.origin;
  const dismissUrl = `${baseUrl}/my-logos/public/admin/updates/notifications/dismiss`;

