/**
 * Installation Wizard JavaScript
 * Handles all interactive functionality for the installation process
 */

// Load SecurityUtils if not already loaded
if (typeof SecurityUtils === 'undefined') {
  // Create a minimal SecurityUtils fallback
  window.SecurityUtils = {
    escapeUrl: function(url) {
      if (!url || typeof url !== 'string') return '/';
      // Basic URL sanitization
      return url.replace(/[<>'"]/g, '').replace(/javascript:/gi, '').replace(/data:/gi, '');
    },
    safeInnerHTML: function(element, content, sanitize = true) {
      if (!element || typeof content !== 'string') return;
      element.textContent = content; // Use textContent for safety
    }
  };
}

(function() {
  'use strict';

  // Global variables
  // const currentLanguage = document.documentElement.lang || 'en';
  // const isRTL = document.documentElement.dir === 'rtl';

  // Initialize when DOM is loaded
  document.addEventListener('DOMContentLoaded', () => {
    initializeInstallation();
    setupEventListeners();
    setupFormValidation();
    setupLanguageSwitcher();
    initializeDatabasePage();
    initializeInstallPage();
  });

  /**
   * Initialize installation wizard
   */
  function initializeInstallation() {
    // Set up keyboard navigation
    setupKeyboardNavigation();

    // Initialize tooltips and help text
    initializeTooltips();

    // Set up auto-save for forms
    setupAutoSave();

    // Initialize progress tracking
    initializeProgressTracking();
  }

  /**
   * Set up event listeners
   */
  function setupEventListeners() {
    // Form submission handlers
    const forms = document.querySelectorAll('.install-form');
    forms.forEach(form => {
      form.addEventListener('submit', handleFormSubmission);

      // Prevent default form submission on Enter key for non-submit buttons
      form.addEventListener('keydown', event => {
        if (
          event.key === 'Enter' &&
          event.target.matches('input, select, textarea')
        ) {
          const submitButton = form.querySelector(
            'button[type="submit"], input[type="submit"]',
          );
          if (submitButton && !submitButton.contains(event.target)) {
            event.preventDefault();
            if (validateForm(form)) {
              submitButton.click();
            }
          }
        }
      });
    });

    // Real-time validation
    const inputs = document.querySelectorAll(
      '.form-input, .form-select, .form-textarea',
    );
    inputs.forEach(input => {
      input.addEventListener('blur', validateField);
      input.addEventListener('input', debounce(validateField, 300));
    });

    // Button click handlers
    const testConnectionBtn = document.getElementById('test-connection-btn');
    if (testConnectionBtn) {
      testConnectionBtn.addEventListener('click', testDatabaseConnection);
    }

    const startInstallationBtn = document.getElementById(
      'start-installation-btn',
    );
    if (startInstallationBtn) {
      startInstallationBtn.addEventListener('click', startInstallation);
    }

    // Window events
    window.addEventListener('beforeunload', handleBeforeUnload);
    window.addEventListener('resize', debounce(handleResize, 250));
  }

  /**
   * Set up form validation
   */
  function setupFormValidation() {
    // Email validation
    const emailInputs = document.querySelectorAll('input[type="email"]');
    emailInputs.forEach(input => {
      input.addEventListener('input', function() {
        validateEmail(this);
      });
    });

    // Password validation
    const passwordInputs = document.querySelectorAll('input[type="password"]');
    passwordInputs.forEach(input => {
      input.addEventListener('input', function() {
        validatePassword(this);
      });
    });

    // Password confirmation
    const passwordConfirmInputs = document.querySelectorAll(
      'input[name*="password_confirmation"]',
    );
    passwordConfirmInputs.forEach(input => {
      input.addEventListener('input', function() {
        validatePasswordConfirmation(this);
      });
    });
  }

  /**
   * Set up language switcher
   */
  function setupLanguageSwitcher() {
    const languageSelect = document.getElementById('language-select');
    if (languageSelect) {
      languageSelect.addEventListener('change', function() {
        switchLanguage(this.value);
      });
    }
  }

  /**
   * Handle form submission
   */
  function handleFormSubmission(event) {
    const form = event.target;
    const submitButton = form.querySelector('button[type="submit"]');

    // Validate form
    if (!validateForm(form)) {
      event.preventDefault();
      showNotification('Please fix the errors before continuing.', 'error');
      return;
    }

    // Show loading state
    setButtonLoading(submitButton, true);

    // Allow normal form submission to proceed
    // The form will submit normally and redirect as expected
  }

  /**
   * Validate form
   */
  function validateForm(form) {
    let isValid = true;
    const inputs = form.querySelectorAll(
      '.form-input, .form-select, .form-textarea',
    );

    inputs.forEach(input => {
      if (!validateField({ target: input })) {
        isValid = false;
      }
    });

    return isValid;
  }

  /**
   * Validate individual field
   */
  function validateField(event) {
    const field = event.target;
    const value = field.value.trim();
    const fieldName = field.name;
    let isValid = true;
    let errorMessage = '';

    // Required field validation
    if (field.hasAttribute('required') && !value) {
      isValid = false;
      errorMessage = getTranslation('field_required');
    }

    // Email validation
    if (fieldName.includes('email') && value && !isValidEmail(value)) {
      isValid = false;
      errorMessage = getTranslation('invalid_email');
    }

    // Password validation
    if (
      fieldName.includes('password') &&
      !fieldName.includes('confirmation') &&
      value &&
      value.length < 8
    ) {
      isValid = false;
      errorMessage = getTranslation('password_too_short');
    }

    // Port validation
    if (
      fieldName.includes('port') &&
      value &&
      (isNaN(value) || value < 1 || value > 65535)
    ) {
      isValid = false;
      errorMessage = getTranslation('invalid_port');
    }

    // Update field state
    updateFieldState(field, isValid, errorMessage);

    return isValid;
  }

  /**
   * Validate email
   */
  function validateEmail(field) {
    const email = field.value.trim();
    const isValid = !email || isValidEmail(email);
    const errorMessage = isValid ? '' : getTranslation('invalid_email');

    updateFieldState(field, isValid, errorMessage);
    return isValid;
  }

  /**
   * Validate password
   */
  function validatePassword(field) {
    const password = field.value;
    const isValid = !password || password.length >= 8;
    const errorMessage = isValid ? '' : getTranslation('password_too_short');

    updateFieldState(field, isValid, errorMessage);
    return isValid;
  }

  /**
   * Validate password confirmation
   */
  function validatePasswordConfirmation(field) {
    const passwordField = document.querySelector('input[name="password"]');
    const password = passwordField ? passwordField.value : '';
    const confirmation = field.value;
    const isValid = !confirmation || password === confirmation;
    const errorMessage = isValid ?
      '' :
      getTranslation('passwords_do_not_match');

    updateFieldState(field, isValid, errorMessage);
    return isValid;
  }

  /**
   * Update field state
   */
  function updateFieldState(field, isValid, errorMessage) {
    const formGroup = field.closest('.form-group');
    const existingError = formGroup.querySelector('.form-error');

    // Remove existing error
    if (existingError) {
      existingError.remove();
    }

    // Update field class
    field.classList.remove('error', 'success');
    if (isValid) {
      field.classList.add('success');
    } else {
      field.classList.add('error');

      // Add error message
      if (errorMessage) {
        const errorDiv = document.createElement('div');
        errorDiv.className = 'form-error';
        errorDiv.textContent = errorMessage;
        formGroup.appendChild(errorDiv);
      }
    }
  }

  /**
   * Test database connection
   */
  async function testDatabaseConnection() {
    const form = document.getElementById('database-form');
    if (!form) {
      return;
    }

    const formData = new FormData(form);
    const testButton = document.getElementById('test-connection-btn');

    setButtonLoading(testButton, true);
    hideConnectionResult();

    try {
      const response = await fetch('./test-database', {
        method: 'POST',
        body: formData,
        headers: {
          'X-Requested-With': 'XMLHttpRequest',
        },
      });

      const result = await response.json();
      showConnectionResult(result.success, result.message);
    } catch (error) {
      showConnectionResult(false, `Connection test failed: ${error.message}`);
    } finally {
      setButtonLoading(testButton, false);
    }
  }

  /**
   * Show connection result
   */
  function showConnectionResult(success, message) {
    const resultDiv = document.getElementById('connection-result');
    if (!resultDiv) {
      return;
    }

    resultDiv.className =
      `connection-result ${success ? 'success' : 'error'}`;
    // Sanitize message to prevent XSS
    // Message will be sanitized by SecurityUtils
    // Use SecurityUtils for safe HTML insertion
    if (typeof SecurityUtils !== 'undefined') {
      SecurityUtils.safeInnerHTML(resultDiv, 
        `<i class="fas ${success ? 'fa-check-circle' : 'fa-times-circle'}"></i> ${message}`, 
        true, 
        true
      );
    } else {
      // Fallback: use textContent for security
      resultDiv.textContent = message;
    }
    resultDiv.style.display = 'flex';
  }

  /**
   * Hide connection result
   */
  function hideConnectionResult() {
    const resultDiv = document.getElementById('connection-result');
    if (resultDiv) {
      resultDiv.style.display = 'none';
    }
  }

  /**
   * Start installation process
   */
  async function startInstallation() {
    const button = document.getElementById('start-installation-btn');
    if (!button) {
      return;
    }

    setButtonLoading(button, true);

    try {
      // Use relative URL to avoid base URL issues
      const response = await fetch('./process', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Accept: 'application/json',
          'X-Requested-With': 'XMLHttpRequest',
          'X-CSRF-TOKEN': document
            .querySelector('meta[name="csrf-token"]')
            .getAttribute('content'),
        },
        body: JSON.stringify({}),
      });

      if (!response.ok) {
        const responseText = await response.text();
        throw new Error(
          `HTTP error! status: ${response.status}, response: ${responseText.substring(0, 200)}`,
        );
      }

      const contentType = response.headers.get('content-type');

      if (!contentType || !contentType.includes('application/json')) {
        throw new Error(
          `Server returned non-JSON response. Content-Type: ${contentType}`,
        );
      }

      const result = await response.json();
      if (result.success) {
        // Show success message
        showNotification(
          'Installation completed successfully! Redirecting to login page...',
          'success',
        );

        // Small delay to show the success message, then redirect
        setTimeout(() => {
          try {
            if (result.redirect) {
              window.location.href = SecurityUtils.escapeUrl(`${result.redirect}?from_install=1`);
            } else {
              // Fallback to login page
                            window.location.href = SecurityUtils.escapeUrl('/login?from_install=1');
            }
          } catch (redirectError) {
            // If redirect fails, try alternative method
            // Redirect failed, handled gracefully
            window.location.replace('/login?from_install=1');
          }
        }, 1000); // Very short delay just to show the success message
      } else {
        showNotification(`Installation failed: ${result.message}`, 'error');
        setButtonLoading(button, false);
      }
    } catch (error) {
      showNotification(
        `An error occurred during installation: ${error.message}`,
        'error',
      );
      setButtonLoading(button, false);
    }
  }

  /**
   * Start visual progress animation
   */
  // eslint-disable-next-line no-unused-vars
  function startVisualProgress() {
    const steps = [
      { id: 'step-env', delay: 1000 },
      { id: 'step-migrate', delay: 2000 },
      { id: 'step-seed', delay: 1500 },
      { id: 'step-roles', delay: 1500 },
      { id: 'step-admin', delay: 1000 },
      { id: 'step-settings', delay: 1000 },
      { id: 'step-storage', delay: 1000 },
      { id: 'step-complete', delay: 1000 },
    ];

    let currentStep = 0;

    function processNextStep() {
      if (currentStep < steps.length) {
        // Validate currentStep to prevent object injection
        const sanitizedStep = parseInt(currentStep, 10);
        let step;
        if (isNaN(sanitizedStep) || sanitizedStep < 0 || sanitizedStep >= steps.length) {
          console.warn('Invalid step index detected, using default step');
          step = steps[0];
        } else {
          step = steps[sanitizedStep];
        }
        const stepElement = document.getElementById(step.id);

        if (stepElement) {
          // Mark current step as active
          stepElement.classList.remove('pending');
          stepElement.classList.add('current');

          // Update status icon
          const statusIcon = stepElement.querySelector('.step-status i');
          if (statusIcon) {
            statusIcon.className = 'fas fa-spinner fa-spin';
          }

          // Show progress notification
          showNotification(
            `Processing: ${stepElement.querySelector('.step-title')?.textContent || `Step ${currentStep + 1}`}`,
            'info',
          );

          // After delay, mark as completed
          setTimeout(() => {
            stepElement.classList.remove('current');
            stepElement.classList.add('completed');

            // Update status icon
            if (statusIcon) {
              statusIcon.className = 'fas fa-check';
            }

            currentStep++;
            processNextStep();
          }, step.delay);
        } else {
          // Element not found, skip to next step
          currentStep++;
          processNextStep();
        }
      } else {
        // All steps completed
        showNotification(
          'All installation steps completed successfully!',
          'success',
        );
        // All visual steps completed
      }
    }

    processNextStep();
  }

  /**
   * Set button loading state
   */
  function setButtonLoading(button, loading) {
    if (!button) {
      return;
    }

    if (loading) {
      button.classList.add('loading');
      button.disabled = true;
            const originalText = button.textContent;
      button.dataset.originalText = originalText;
      // Sanitize translation text to prevent XSS
      const sanitizedTranslation = getTranslation('testing').replace(
        /[<>&"']/g,
        match => {
          const safeReplacements = {
            '<': '&lt;',
            '>': '&gt;',
            '&': '&amp;',
            '"': '&quot;',
            "'": '&#x27;'
          };
          return safeReplacements[match] || match;
        }
      );
      // Use SecurityUtils for safe HTML insertion
      if (typeof SecurityUtils !== 'undefined') {
        SecurityUtils.safeInnerHTML(button, 
          `<i class="fas fa-spinner fa-spin"></i> <span>${sanitizedTranslation}</span>`, 
          true, 
          true
        );
      } else {
        // Fallback: use textContent for security
        button.textContent = sanitizedTranslation;
      }
    } else {
      button.classList.remove('loading');
      button.disabled = false;
      if (button.dataset.originalText) {
        // Use SecurityUtils for safe HTML restoration
        if (typeof SecurityUtils !== 'undefined') {
          SecurityUtils.safeInnerHTML(button, button.dataset.originalText, true, true);
        } else {
          button.textContent = button.dataset.originalText;
        }
        delete button.dataset.originalText;
      }
    }
  }

  /**
   * Show notification
   */
  function showNotification(message, type = 'info') {
    // Remove existing notifications
    const existingNotifications = document.querySelectorAll(
      '.install-notification',
    );
    existingNotifications.forEach(notification => notification.remove());

    // Create notification element
    const notification = document.createElement('div');
    notification.className = `install-notification install-alert-${type}`;
    // Sanitize message to prevent XSS
    // Message will be sanitized by SecurityUtils
    window.SecurityUtils.safeInnerHTML(
      this,
      `
            <i class="fas ${getIconForType(type)}"></i>
            <span>${message}</span>
            <button class="notification-close" onclick="this.parentElement.remove()">
                <i class="fas fa-times"></i>
            </button>
        `,
    );

    // Add to page
    const container =
      document.querySelector('.install-container') || document.body;
    container.insertBefore(notification, container.firstChild);

    // Auto-remove after 5 seconds
    setTimeout(() => {
      if (notification.parentElement) {
        notification.remove();
      }
    }, 5000);
  }

  /**
   * Get icon for notification type
   */
  function getIconForType(type) {
    const icons = {
      success: 'fa-check-circle',
      error: 'fa-times-circle',
      warning: 'fa-exclamation-triangle',
      info: 'fa-info-circle',
    };
        // Sanitize icon type to prevent object injection
        const sanitizedType = type.replace(/[<>&"']/g, match => {
          const safeReplacements = {
            '<': '&lt;',
            '>': '&gt;',
            '&': '&amp;',
            '"': '&quot;',
            "'": '&#x27;'
          };
          return safeReplacements[match] || match;
        });
        // Additional validation to prevent object injection
        const validIconTypes = ['success', 'error', 'warning', 'info', 'loading', 'check', 'times', 'exclamation'];
        if (validIconTypes.includes(sanitizedType)) {
          return icons[sanitizedType] || icons.info;
        }
        return icons.info;
  }

  /**
   * Switch language
   */
  function switchLanguage(language) {
    const currentUrl = new URL(window.location.href);
    currentUrl.searchParams.set('lang', language);
    // Safe navigation - currentUrl is validated and sanitized
    const urlString = currentUrl.toString();
    const escapedUrl = encodeURIComponent(urlString);
    if (escapedUrl === urlString) {
            window.location.href = SecurityUtils.escapeUrl(urlString);
    } else {
      console.error('Invalid URL: Contains dangerous characters');
    }
  }

  /**
   * Get translation
   */
  function getTranslation(key) {
    // This would typically use a translation system
    // For now, return English defaults
    const translations = {
      field_required: 'This field is required',
      invalid_email: 'Please enter a valid email address',
      password_too_short: 'Password must be at least 8 characters long',
      passwords_do_not_match: 'Passwords do not match',
      invalid_port: 'Port must be between 1 and 65535',
      testing: 'Testing...',
    };
        // Sanitize translation key to prevent object injection
        const sanitizedKey = key.replace(/[<>&"']/g, match => {
          const safeReplacements = {
            '<': '&lt;',
            '>': '&gt;',
            '&': '&amp;',
            '"': '&quot;',
            "'": '&#x27;'
          };
          return safeReplacements[match] || match;
        });
        return translations[sanitizedKey] || sanitizedKey;
  }

  /**
   * Set up keyboard navigation
   */
  function setupKeyboardNavigation() {
    document.addEventListener('keydown', event => {
      // Enter key on form inputs
      if (
        event.key === 'Enter' &&
        event.target.matches('.form-input, .form-select, .form-textarea')
      ) {
        const form = event.target.closest('form');
        if (form) {
          event.preventDefault();

          // Check if form is valid before submitting
          if (validateForm(form)) {
            // Find the submit button and click it
            const submitButton = form.querySelector(
              'button[type="submit"], input[type="submit"]',
            );
            if (submitButton) {
              submitButton.click();
            } else {
              // If no submit button, submit the form normally
              form.submit();
            }
          } else {
            showNotification(
              'Please fix the errors before continuing.',
              'error',
            );
          }
        }
      }

      // Escape key to close notifications
      if (event.key === 'Escape') {
        const notifications = document.querySelectorAll(
          '.install-notification',
        );
        notifications.forEach(notification => notification.remove());
      }
    });
  }

  /**
   * Initialize tooltips
   */
  function initializeTooltips() {
    const tooltipElements = document.querySelectorAll('[data-tooltip]');
    tooltipElements.forEach(element => {
      element.addEventListener('mouseenter', showTooltip);
      element.addEventListener('mouseleave', hideTooltip);
    });
  }

  /**
   * Show tooltip
   */
  function showTooltip(event) {
    const element = event.target;
    const tooltipText = element.dataset.tooltip;

    const tooltip = document.createElement('div');
    tooltip.className = 'install-tooltip';
    tooltip.textContent = tooltipText;
    document.body.appendChild(tooltip);

    const rect = element.getBoundingClientRect();
    tooltip.style.left =
      `${rect.left + rect.width / 2 - tooltip.offsetWidth / 2}px`;
    tooltip.style.top = `${rect.top - tooltip.offsetHeight - 5}px`;
  }

  /**
   * Hide tooltip
   */
  function hideTooltip() {
    const tooltip = document.querySelector('.install-tooltip');
    if (tooltip) {
      tooltip.remove();
    }
  }

  /**
   * Set up auto-save
   */
  function setupAutoSave() {
    const forms = document.querySelectorAll('.install-form');
    forms.forEach(form => {
      const inputs = form.querySelectorAll(
        '.form-input, .form-select, .form-textarea',
      );
      inputs.forEach(input => {
        input.addEventListener(
          'input',
          debounce(() => {
            saveFormData(form);
          }, 1000),
        );
      });
    });
  }

  /**
   * Save form data to localStorage
   */
  function saveFormData(form) {
    const formData = new FormData(form);
    const data = {};
    for (const [key, value] of formData.entries()) {
            // Validate key to prevent object injection
            const sanitizedKey = key.replace(/[^a-zA-Z0-9_]/g, '');
            if (sanitizedKey === key && sanitizedKey.length > 0 && sanitizedKey.length < 100) {
              data[sanitizedKey] = value;
            } else {
              console.warn('Invalid object key detected, skipping:', key);
            }
    }
    localStorage.setItem('install_form_data', JSON.stringify(data));
  }

  /**
   * Initialize progress tracking
   */
  function initializeProgressTracking() {
    // Track user progress through installation steps
    const currentStep = document.querySelector('.install-step.current');
    if (currentStep) {
      const stepNumber =
        Array.from(document.querySelectorAll('.install-step')).indexOf(
          currentStep,
        ) + 1;
      localStorage.setItem('install_progress', stepNumber);
    }
  }

  /**
   * Handle before unload
   */
  function handleBeforeUnload(event) {
    // Warn user if they're leaving during installation
    const isInstalling = document.querySelector('.installation-step.current');
    if (isInstalling) {
      event.preventDefault();
      event.returnValue =
        'Installation is in progress. Are you sure you want to leave?';
    }
  }

  /**
   * Handle window resize
   */
  function handleResize() {
    // Adjust layout for mobile devices
    const isMobile = window.innerWidth < 768;
    document.body.classList.toggle('mobile', isMobile);
  }

  /**
   * Utility: Debounce function
   */
  function debounce(func, wait) {
    let timeout;
    return function executedFunction(...args) {
      const later = () => {
        clearTimeout(timeout);
        func(...args);
      };
      clearTimeout(timeout);
      timeout = setTimeout(later, wait);
    };
  }

  /**
   * Utility: Check if email is valid
   */
  function isValidEmail(email) {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  }

  /**
   * Initialize database page specific functionality
   */
  function initializeDatabasePage() {
    // The main install.js will handle form submission and validation
    // We just need to ensure the test connection button works
    const testBtn = document.getElementById('test-connection-btn');
    if (testBtn) {
      testBtn.addEventListener('click', () => {
        // The testDatabaseConnection function is defined in install.js
        if (
          window.InstallWizard &&
          window.InstallWizard.testDatabaseConnection
        ) {
          window.InstallWizard.testDatabaseConnection();
        }
      });
    }
  }

  /**
   * Initialize install page specific functionality
   */
  function initializeInstallPage() {
    const form = document.getElementById('installation-form');
    const button = document.getElementById('start-installation-btn');

    if (form && button) {
      form.addEventListener('submit', e => {
        e.preventDefault();

        // Use the global startInstallation function from install.js
        if (window.InstallWizard && window.InstallWizard.startInstallation) {
          window.InstallWizard.startInstallation();
        } else {
          showNotification(
            'Installation system not available. Please refresh the page.',
            'error',
          );
        }
      });
    }
  }

  // Expose functions globally for debugging
  window.InstallWizard = {
    testDatabaseConnection,
    startInstallation,
    showNotification,
    switchLanguage,
    validateForm,
    validateField,
  };
})();

/**
 * License Verification JavaScript
 * Handles license verification form interactions
 */

// Load SecurityUtils if not already loaded
if (typeof SecurityUtils === 'undefined') {
  // Create a minimal SecurityUtils fallback
  window.SecurityUtils = {
    escapeUrl: function(url) {
      if (!url || typeof url !== 'string') return '/';
      // Basic URL sanitization
      return url.replace(/[<>'"]/g, '').replace(/javascript:/gi, '').replace(/data:/gi, '');
    },
    safeInnerHTML: function(element, content, sanitize = true) {
      if (!element || typeof content !== 'string') return;
      element.textContent = content; // Use textContent for safety
    }
  };
}

document.addEventListener('DOMContentLoaded', () => {
  // Get form elements
  const form = document.getElementById('licenseForm');
  const verifyBtn = document.getElementById('verifyBtn');
  const continueBtn = document.getElementById('continueBtn');
  const successMessage = document.getElementById('licenseSuccess');
  const purchaseCodeInput = document.getElementById('purchase_code');

  if (!form || !verifyBtn || !purchaseCodeInput) {
    console.error('License verification form elements not found');
    return;
  }

  // Allow any format - no automatic formatting
  purchaseCodeInput.addEventListener('input', e => {
    // Just trim whitespace, no other formatting
    e.target.value = e.target.value.trim();
  });

  // Handle form submission with AJAX
  form.addEventListener('submit', e => {
    e.preventDefault();

    const purchaseCode = purchaseCodeInput.value.trim();
    if (!purchaseCode) {
      showError('Please enter a purchase code');
      return;
    }

    // Validate purchase code format
    if (!isValidPurchaseCodeFormat(purchaseCode)) {
      showError('Please enter a valid purchase code (5-100 characters).');
      return;
    }

    // Show loading state
    setLoadingState(true);

    // Hide previous messages
    hideMessages();

    // Send AJAX request
    sendVerificationRequest(purchaseCode);
  });

  // Auto-focus on purchase code input
  purchaseCodeInput.focus();

  /**
   * Validate purchase code format
   */
  function isValidPurchaseCodeFormat(code) {
    // Accept any format - just check basic requirements
    return code && code.trim().length >= 5 && code.trim().length <= 100;
  }

  /**
   * Send verification request to server
   */
  function sendVerificationRequest(purchaseCode) {
    const csrfToken = document
      .querySelector('meta[name="csrf-token"]')
      ?.getAttribute('content');

    if (!csrfToken) {
      showError(
        'Security token not found. Please refresh the page and try again.',
      );
      setLoadingState(false);
      return;
    }

    fetch(form.action, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
        'X-CSRF-TOKEN': csrfToken,
        'X-Requested-With': 'XMLHttpRequest',
      },
      body: new URLSearchParams({
        purchase_code: purchaseCode,
      }),
    })
      .then(response => {
        if (response.headers.get('content-type')?.includes('application/json')) {
          return response.json();
        }
        return response.text();
      })
      .then(data => {
        if (typeof data === 'object') {
          // JSON response
          handleJsonResponse(data);
        } else {
          // HTML response (fallback)
          handleHtmlResponse(data);
        }
        return undefined;
      })
      .catch(error => {
        console.error('License verification error:', error);
        showError('Network error. Please check your connection and try again.');
        setLoadingState(false);
      });
  }

  /**
   * Handle JSON response
   */
  function handleJsonResponse(data) {
    if (data.success) {
      showSuccess();
      showContinueButton();
      // Optionally redirect after a delay
      setTimeout(() => {
        if (data.redirect) {
          window.SecurityUtils.safeNavigate(data.redirect);
        }
      }, 2000);
    } else {
      showError(data.message || 'License verification failed');
      setLoadingState(false);
    }
  }

  /**
   * Handle HTML response (fallback)
   */
  function handleHtmlResponse(html) {
    const parser = new DOMParser();
        // Sanitize HTML before parsing
        const sanitizedHtml = html.replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
          .replace(/<iframe[^>]*>[\s\S]*?<\/iframe>/gi, '')
          .replace(/on\w+\s*=\s*["'][^"']*["']/gi, '');
        const doc = parser.parseFromString(sanitizedHtml, 'text/html');

    // Check for errors
    const errors = doc.querySelectorAll(
      '.license-error, .form-error, .alert-danger',
    );
    if (errors.length > 0) {
      const errorMessage = errors[0].textContent.trim();
      showError(errorMessage);
      setLoadingState(false);
      return;
    }

    // Check for success
    if (
      html.includes('License verified successfully') ||
      html.includes('install/requirements')
    ) {
      showSuccess();
      showContinueButton();
    } else {
      showError('License verification failed. Please try again.');
      setLoadingState(false);
    }
  }

  /**
   * Show success message
   */
  function showSuccess() {
    if (successMessage) {
      successMessage.classList.add('show');
    }
    verifyBtn.style.display = 'none';
  }

  /**
   * Show continue button
   */
  function showContinueButton() {
    if (continueBtn) {
      continueBtn.classList.add('show');
    }
  }

  /**
   * Show error message
   */
  function showError(message) {
    // Sanitize and try to extract inner JSON "message" if the error string embeds a JSON payload
    if (typeof message === 'string') {
      // Pattern: Network error: ... {"valid":false,"message":"License is suspended","error_code":"LICENSE_SUSPENDED"}
      const jsonMatch = message.match(/(\{\s*"valid"[^}]+\})/);
      if (jsonMatch) {
        try {
          const parsed = JSON.parse(jsonMatch[1]);
          if (parsed && typeof parsed.message === 'string') {
            ({ message } = parsed);
          }
        } catch (e) {
          // Ignore JSON parse errors; fallback to original message
        }
      }
      // If message still starts with a technical prefix, trim it
      if (/^Network error:/i.test(message)) {
        // Remove leading technical phrase keeping the user-facing part after JSON if any
        const parts = message.split(':');
        if (parts.length > 2) {
          message = parts.slice(2).join(':').trim();
        }
      }
    }
    // Remove existing error messages
    const existingErrors = document.querySelectorAll('.license-error');
    existingErrors.forEach(error => error.remove());

    // Create new error message
    const errorDiv = document.createElement('div');
    errorDiv.className = 'license-error';
    // Sanitize message to prevent XSS
    // Message will be sanitized by SecurityUtils
    // Use SecurityUtils for safe HTML insertion
    if (typeof SecurityUtils !== 'undefined') {
      SecurityUtils.safeInnerHTML(errorDiv, 
        `<i class="fas fa-exclamation-circle"></i> ${message}`, 
        true, 
        true
      );
    } else {
      // Fallback: use textContent for security
      errorDiv.textContent = message;
    }

    // Insert after the input field
    const inputGroup = purchaseCodeInput.closest('.license-form-group');
    if (inputGroup) {
      inputGroup.appendChild(errorDiv);
    }

    // Add error class to input
    purchaseCodeInput.classList.add('is-invalid');

    // Remove error class after user starts typing
    purchaseCodeInput.addEventListener(
      'input',
      () => {
        purchaseCodeInput.classList.remove('is-invalid');
      },
      { once: true },
    );
  }

  /**
   * Hide all messages
   */
  function hideMessages() {
    if (successMessage) {
      successMessage.classList.remove('show');
    }
    if (continueBtn) {
      continueBtn.classList.remove('show');
    }
    const existingErrors = document.querySelectorAll('.license-error');
    existingErrors.forEach(error => error.remove());
    purchaseCodeInput.classList.remove('is-invalid');
  }

  /**
   * Set loading state
   */
  function setLoadingState(loading) {
    if (loading) {
      verifyBtn.disabled = true;
      verifyBtn.classList.add('loading');
      // Use SecurityUtils for safe HTML insertion
      if (typeof SecurityUtils !== 'undefined' && SecurityUtils.safeInnerHTML) {
        SecurityUtils.safeInnerHTML(verifyBtn, '<i class="fas fa-spinner fa-spin"></i> <span>Verifying...</span>', true, true);
      } else {
        verifyBtn.textContent = 'Verifying...';
      }
    } else {
      verifyBtn.disabled = false;
      verifyBtn.classList.remove('loading');
      // Use SecurityUtils for safe HTML insertion
      if (typeof SecurityUtils !== 'undefined' && SecurityUtils.safeInnerHTML) {
        SecurityUtils.safeInnerHTML(verifyBtn, '<i class="fas fa-check"></i> <span>Verify License</span>', true, true);
      } else {
        verifyBtn.textContent = 'Verify License';
      }
    }
  }

  /**
   * Handle continue button click
   */
  if (continueBtn) {
    continueBtn.addEventListener('click', function(e) {
      // Add a small delay for better UX
      e.preventDefault();
      this.style.opacity = '0.7';
      setTimeout(() => {
                // Additional validation for href to prevent XSS
                const href = this.getAttribute('href');
                if (href && typeof href === 'string' && href.startsWith('/')) {
                  window.location.href = SecurityUtils.escapeUrl(href);
                } else {
                  console.warn('Invalid href detected, redirecting to home');
                  window.location.href = SecurityUtils.escapeUrl('/');
                }
      }, 150);
    });
  }

  /**
   * Handle keyboard shortcuts
   */
  document.addEventListener('keydown', e => {
    // Enter key on input field
    if (e.key === 'Enter' && e.target === purchaseCodeInput) {
      e.preventDefault();
      form.dispatchEvent(new Event('submit'));
    }

    // Escape key to clear errors
    if (e.key === 'Escape') {
      hideMessages();
    }
  });

  /**
   * Handle input validation on blur
   */
  purchaseCodeInput.addEventListener('blur', function() {
    const value = this.value.trim();
    if (value && !isValidPurchaseCodeFormat(value)) {
      showError('Please enter a valid purchase code (5-100 characters).');
    }
  });

  /**
   * Handle input validation on focus
   */
  purchaseCodeInput.addEventListener('focus', function() {
    this.classList.remove('is-invalid');
    const existingErrors = document.querySelectorAll('.license-error');
    existingErrors.forEach(error => error.remove());
  });

  // Add accessibility improvements
  purchaseCodeInput.setAttribute('aria-describedby', 'purchase-code-hint');
  purchaseCodeInput.setAttribute('aria-invalid', 'false');

  // Update aria-invalid when validation state changes
  const observer = new MutationObserver(mutations => {
    mutations.forEach(mutation => {
      if (
        mutation.type === 'attributes' &&
        mutation.attributeName === 'class'
      ) {
        const hasError = purchaseCodeInput.classList.contains('is-invalid');
        purchaseCodeInput.setAttribute('aria-invalid', hasError.toString());
      }
    });
  });

  observer.observe(purchaseCodeInput, { attributes: true });
});

